import React, { useState } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
  };

  const lineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    bottom: 0,
  };

  const menuStyle = {
    listStyle: 'none',
    display: menuOpen ? 'flex' : 'none',
    flexDirection: 'column',
    margin: 0,
    padding: 0,
    position: 'absolute',
    top: '100%',
    left: 0,
    backgroundColor: 'white',
    width: '100%',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
  };

  const menuItemStyle = {
    margin: '10px 0',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  const menuIconStyle = {
    cursor: 'pointer',
    position: 'relative',
    width: '24px', // Ancho del ícono
    height: '24px', // Alto del ícono
  };

  const menuIconLineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    transition: '0.3s',
  };

  if (menuOpen) {
    menuIconLineStyle.transform = 'rotate(-45deg)';
    menuIconLineStyle.top = '11px';
  }

  return (
    <nav style={navbarStyle}>
      <div style={menuIconStyle} onClick={toggleMenu}>
        <div style={{ ...menuIconLineStyle, top: '0' }}></div>
        <div style={menuIconLineStyle}></div>
        <div style={{ ...menuIconLineStyle, top: '11px' }}></div>
      </div>
      {/* Mostrar el menú junto al botón cuando la pantalla es lo suficientemente grande */}
      <ul style={{ ...menuStyle, display: window.innerWidth >= 768 ? 'flex' : 'none' }}>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Inicio</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Información General</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Registrarse</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Contáctenos</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
