import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleResize = () => {
    setIsSmallScreen(window.innerWidth < 768);
  };

  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
  };

  const menuIconStyle = {
    display: 'none',
  };

  const menuItemStyle = {
    margin: '0 10px', // Espacio horizontal entre elementos
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'block' : 'none') : 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    margin: 0,
    padding: 0,
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light" style={navbarStyle}>
      <button
        className="navbar-toggler"
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#navbarNav"
        onClick={toggleMenu}
      >
        <span className="navbar-toggler-icon"></span>
      </button>
      {isSmallScreen ? (
        <div style={menuIconStyle}>
          <div style={{ ...menuIconLineStyle, top: '0' }}></div>
          <div style={menuIconLineStyle}></div>
          <div style={{ ...menuIconLineStyle, top: '11px' }}></div>
        </div>
      ) : (
        <ul className="navbar-nav" style={menuStyle}>
          <li className="nav-item" style={menuItemStyle}>
            <a className="nav-link" href="#" style={linkStyle}>
              Inicio
            </a>
          </li>
          <li className="nav-item" style={menuItemStyle}>
            <a className="nav-link" href="#" style={linkStyle}>
              Información General
            </a>
          </li>
          <li className="nav-item" style={menuItemStyle}>
            <a className="nav-link" href="#" style={linkStyle}>
              Registrarse
            </a>
          </li>
          <li className="nav-item" style={menuItemStyle}>
            <a className="nav-link" href="#" style={linkStyle}>
              Contáctenos
            </a>
          </li>
        </ul>
      )}
    </nav>
  );
};

export default Navbar;
