import React, { useState, useEffect } from 'react';
import './Navbar.css'; // Importa el archivo CSS

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const topBarStyle = {
    backgroundColor: '#4179CC',
    height: '20px',
    width: '100%',
  };

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
    top: '4px',
  };

  const lineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    bottom: 0,
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'block' : 'none') : 'flex',
    flexDirection: isSmallScreen ? 'column' : 'row',
    margin: 0,
    padding: 0,
    position: 'absolute',
    top: isSmallScreen ? '100%' : 20,
    left: isSmallScreen ? 0 : '40px',
    backgroundColor: 'white',
    width: '100%',
  };

  const menuItemStyle = {
    margin: '0 10px',
    backgroundColor: menuOpen ? 'transparent' : 'white', // Cambia el fondo a blanco si el menú está abierto
    transition: 'background-color 0.3s', // Agrega transición para el cambio de color
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  const hamburgerStyle = {
    cursor: 'pointer',
    fontSize: '30px',
    color: '#4179cc',
  };

  return (
    <div>
      <div style={topBarStyle}></div>
      <nav style={navbarStyle}>
        <div style={lineStyle}></div>
        <div onClick={toggleMenu} style={{ ...hamburgerStyle, marginRight: '1px', color: '#4179cc' }}>
          ☰
        </div>
        <ul style={menuStyle}>
          <li
            style={{
              ...menuItemStyle,
              backgroundColor: menuOpen && hoveredMenuItem === 'Contáctenos' ? 'orange' : 'transparent', // Cambia el fondo solo para "Contáctenos"
            }}
          >
            <a href="#" style={linkStyle}>Inicio</a>
          </li>
          <li
            style={menuItemStyle}
          >
            <a href="#" style={linkStyle}>Información General</a>
          </li>
          <li
            style={menuItemStyle}
          >
            <a href="#" style={linkStyle}>Registrarse</a>
          </li>
          <li
            style={menuItemStyle}
          >
            <a href="#" style={linkStyle}>Contáctenos</a>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Navbar;
