import React, { useState } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
  };

  const lineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    bottom: 0,
  };

  const menuStyle = {
    listStyle: 'none',
    display: menuOpen ? 'flex' : 'none', // Mostrar el menú si está abierto
    flexDirection: 'column', // Mostrar las opciones de manera vertical
    margin: 0,
    padding: 0,
    position: 'absolute',
    top: '100%', // Colocar el menú debajo de la barra de navegación
    left: 0,
    backgroundColor: 'white', // Fondo blanco
    width: '100%', // Ancho completo
  };

  const menuItemStyle = {
    margin: '10px 0', // Espacio vertical entre elementos del menú
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  return (
    <nav style={navbarStyle}>
      <div style={lineStyle}></div>
      <div onClick={toggleMenu} style={{ cursor: 'pointer' }}>
        ☰ {/* Icono de hamburguesa */}
      </div>
      <ul style={menuStyle}>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Inicio</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Información General</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Registrarse</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Contáctenos</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
