import React, { useState, useEffect } from 'react';
import './Navbar.css'; // Importa la hoja de estilo CSS
// Asegúrate de que las rutas a las imágenes sean correctas
import logoImage from '../assets/LogoPng.png';
import userIcon from '../assets/user.png';
import cartIcon from '../assets/add-to-cart.png';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navbarContainerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#FDB254',
    padding: '10px 20px',
  };

  const logoAndBrandContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    cursor: 'pointer',
  };

  const brandNameStyle = {
    marginLeft: '10px',
    fontFamily: "'Gravitas One', sans-serif",
    fontSize: '24px',
    textTransform: 'uppercase',
    fontWeight: 'bold',
  };

  const menuStyle = {
    listStyle: 'none',
    display: 'flex',
    justifyContent: 'flex-end', // Alinea los elementos del menú hacia la derecha
    margin: 0,
    padding: 0,
    marginRight: '-500px', // Ajusta este valor según sea necesario para mover las opciones del menú a la derecha
  };

  const menuItemStyle = {
    padding: '0 20px',
    color: '#000000',
    cursor: 'pointer',
    fontFamily: "'Montserrat', sans-serif",
    fontSize: '16px',
  };

  const iconButtonStyle = {
    background: 'none',
    border: 'none',
    cursor: 'pointer',
    padding: '0 10px',
  };

  const iconStyle = {
    height: '30px',
  };
  return (
    <div style={navbarContainerStyle}>
      <div style={logoAndBrandContainerStyle}>
        <img src={logoImage} alt="Logo" style={{ height: '100px' }} />
        <span style={brandNameStyle}>ASIA IMPORTS</span>
      </div>
      <ul style={menuStyle}>
        {/* Utiliza la clase 'menu-item' para los enlaces */}
        <li><a href="#inicio" className="menu-item">INICIO</a></li>
        <li><a href="#informacion" className="menu-item">INFORMACION</a></li>
        <li><a href="#comprar" className="menu-item">COMPRAR</a></li>
      </ul>
      <div>
        {/* Iconos de usuario y carrito */}
        <button style={iconButtonStyle} onClick={() => console.log('User icon clicked')}>
          <img src={userIcon} alt="User" style={iconStyle} />
        </button>
        <button style={iconButtonStyle} onClick={() => console.log('Cart icon clicked')}>
          <img src={cartIcon} alt="Cart" style={iconStyle} />
        </button>
      </div>
    </div>
  );
};

export default Navbar;
