import React from 'react';

const Navbar = () => {
  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
  };

  const lineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    bottom: 0,
  };

  const menuStyle = {
    listStyle: 'none',
    display: 'none', // Ocultar el menú en pantallas grandes
    margin: 0,
    padding: 0,
  };

  const menuItemStyle = {
    margin: '0 10px',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  // Establecer un ancho de pantalla a partir del cual se mostrará el menú
  const screenWidthThreshold = 768;

  // Aplicar estilos de menú desplegable en pantallas pequeñas
  if (window.innerWidth < screenWidthThreshold) {
    menuStyle.display = 'flex';
    menuStyle.flexDirection = 'column';
    menuStyle.position = 'absolute';
    menuStyle.top = '100%';
    menuStyle.left = 0;
    menuStyle.backgroundColor = 'white';
    menuStyle.width = '100%';
    menuStyle.boxShadow = '0px 4px 8px rgba(0, 0, 0, 0.2)';
  }

  return (
    <nav style={navbarStyle}>
      <div style={lineStyle}></div>
      <ul style={menuStyle}>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Inicio</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Información General</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Registrarse</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Contáctenos</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
