import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import './Login.css';

function Login() {
  const [show, setShow] = useState(false);
  const [isLoginSelected, setIsLoginSelected] = useState(true);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleIniciarSesionClick = () => {
    setIsLoginSelected(true);
  };

  const handleRegistrarseClick = () => {
    setIsLoginSelected(false);
  };

  return (
    <>
      <Button onClick={handleShow} className="login-button">
        Iniciar Sesión / Registrarse
      </Button>

      <Modal show={show} onHide={handleClose} size="md" centered>
        <Modal.Header closeButton>
          <Modal.Title>
            <div className="button-container">
              <Button variant="link" onClick={handleIniciarSesionClick} 
                      className={`tab-button ${isLoginSelected ? 'selected' : ''}`}>
                Iniciar Sesión
              </Button>
              <Button variant="link" onClick={handleRegistrarseClick} 
                      className={`tab-button ${!isLoginSelected ? 'selected' : ''}`}>
                Registrarse
              </Button>
            </div>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            {isLoginSelected ? (
              <>
                <Form.Group controlId="formBasicEmail">
                  <Form.Control type="email" placeholder="Correo Electrónico" />
                </Form.Group>
                <Form.Group controlId="formBasicPassword">
                  <Form.Control type="password" placeholder="Contraseña" />
                </Form.Group>
              </>
            ) : (
              <>
                <Form.Group controlId="formBasicNombre">
                  <Form.Control type="text" placeholder="Nombre" />
                </Form.Group>
                <Form.Group controlId="formBasicApellido">
                  <Form.Control type="text" placeholder="Apellido" />
                </Form.Group>
                <Form.Group controlId="formBasicID">
                  <Form.Control type="text" placeholder="ID O RTN" />
                </Form.Group>
                <Form.Group controlId="formBasicEmailRegistro">
                  <Form.Control type="email" placeholder="Correo Electrónico" />
                </Form.Group>
                <Form.Group controlId="formBasicPasswordRegistro">
                  <Form.Control type="password" placeholder="Contraseña" />
                </Form.Group>
                <Form.Group controlId="formBasicConfirmPassword">
                  <Form.Control type="password" placeholder="Confirmar Contraseña" />
                </Form.Group>
              </>
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cerrar
          </Button>
          <Button variant="primary">
            {isLoginSelected ? 'Iniciar Sesión' : 'Registrarse'}
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Login;
