import React, { useState } from 'react';
import { Container, Button, Collapse } from 'react-bootstrap';
import './AdminPage.css';
import menu from '../assets/menu-sidebar.png'

const AdminPage = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  const closeSidebar = () => {
    setSidebarOpen(false);
  };

  return (
    <Container fluid>
      {/* Botón fijo para activar el sidebar */}
      <Button onClick={toggleSidebar} className="toggle-sidebar">
        <img src={menu} alt='toggle_sidebar' className='icon-image'/>
      </Button>

      {/* Sidebar */}
      <Collapse in={isSidebarOpen}>
        <div className="sidebar">
          <div className="row">
            <Button variant="secondary" onClick={closeSidebar}>
              Cerrar Sidebar
            </Button>
            <Button variant="secondary">Botón 2</Button>
            <Button variant="secondary">Botón 3</Button>
            <Button variant="secondary">Botón 4</Button>
            <Button variant="secondary">Botón 5</Button>
          </div>
        </div>
      </Collapse>
    </Container>
  );
};

export default AdminPage;
