import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navbarContainerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#FFA500',
    padding: '10px 20px',
  };

  const logoAndBrandContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    cursor: 'pointer',
  };

  const brandNameStyle = {
    marginLeft: '10px',
    fontFamily: "'Gravitas One', sans-serif", // Se ha quitado 'cursive' para no incluir estilos de cursiva
    fontSize: '24px', // Ajusta según sea necesario
    textTransform: 'uppercase', // Transforma el texto a mayúsculas
    fontWeight: 'bold', // Aplica negrita al texto
  };

  const menuStyle = {
    listStyle: 'none',
    display: 'flex',
    justifyContent: 'flex-end', // Alinea los elementos del menú hacia la derecha
    margin: 0,
    padding: 0,
    marginRight: '-500px', // Ajusta este valor según sea necesario para mover las opciones del menú a la derecha
  };


  const menuItemStyle = {
    padding: '0 20px',
    color: '#000000',
    cursor: 'pointer',
    fontFamily: "'Montserrat', sans-serif",
    fontSize: '16px',
  };

  const logoStyle = {
    height: '80px', // Ajusta el tamaño del logo
    marginRight: '10px', // Añade un margen a la derecha si es necesario
  };

  return (
    <div style={navbarContainerStyle}>
      <div style={logoAndBrandContainerStyle}>
        {/* Ajusta el tamaño del logo según sea necesario */}
        <img src={require('../assets/LogoPng.png')} alt="Logo" style={logoStyle} />
        {/* Texto en mayúsculas y en negrita */}
        <span style={brandNameStyle}>ASIAN IMPORTS</span>
      </div>
      <ul style={menuStyle}>
        <li style={menuItemStyle}>INICIO</li>
        <li style={menuItemStyle}>INFORMACION</li>
        <li style={menuItemStyle}>COMPRAR</li>
      </ul>
      <div>
        <span style={menuItemStyle}>👤</span> {/* Placeholder para el ícono de usuario */}
        <span style={menuItemStyle}>🛒</span> {/* Placeholder para el ícono de carrito */}
      </div>
    </div>
  );
};

export default Navbar;
