import React, { useState } from 'react';
import './EditarPerfil.css'; // Asegúrate de que la ruta al CSS es correcta

// Importa tus iconos aquí
import userIcon from '../assets/user.png'; // Asegúrate de que la ruta al ícono es correcta

const EditarPerfil = () => {
  // Estados para almacenar la información del usuario
  const [nombre, setNombre] = useState('');
  const [apellido, setApellido] = useState('');
  const [id, setId] = useState('');

  // Función para manejar el envío del formulario
  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí manejarías el envío de los datos del formulario, por ejemplo, a un API
    console.log({ nombre, apellido, id });
  };

  return (
    <div className="perfil-container">
      <div className="icono-usuario">
        <img src={userIcon} alt="Ícono de Usuario" />
      </div>
      <form className="formulario-perfil" onSubmit={handleSubmit}>
        <div className="campo-formulario">
          <label htmlFor="nombre">Nombre*</label>
          <input type="text" id="nombre" value={nombre} onChange={(e) => setNombre(e.target.value)} required />
        </div>
        <div className="campo-formulario">
          <label htmlFor="apellido">Apellido*</label>
          <input type="text" id="apellido" value={apellido} onChange={(e) => setApellido(e.target.value)} required />
        </div>
        <div className="campo-formulario">
          <label htmlFor="id">ID*</label>
          <input type="text" id="id" value={id} onChange={(e) => setId(e.target.value)} required />
        </div>
        <div className="campo-formulario">
          <button type="submit" className="boton-guardar">GUARDAR</button>
        </div>
      </form>
    </div>
  );
};

export default EditarPerfil;
