import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import userIcon from '../assets/user.png';
import './Login.css';

function Login() {
  const [show, setShow] = useState(false);
  const [isLoginSelected, setIsLoginSelected] = useState(true);

  // Form state for registration inputs
  const [formState, setFormState] = useState({
    nombre: '',
    apellido: '',
    idOrRtn: '',
    correo: '',
    contrasena: '',
    confirmarContrasena: ''
  });

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleLoginClick = () => {
    setIsLoginSelected(true);
  };

  const handleRegisterClick = () => {
    setIsLoginSelected(false);
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormState(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const renderLoginForm = () => (
    <>
      <Form.Group className="mb-3" controlId="formBasicEmail">
        <Form.Label>Correo Electrónico</Form.Label>
        <Form.Control type="email" placeholder="Ingrese correo" />
      </Form.Group>

      <Form.Group className="mb-3" controlId="formBasicPassword">
        <Form.Label>Contraseña</Form.Label>
        <Form.Control type="password" placeholder="Contraseña" />
      </Form.Group>
    </>
  );

  const renderRegisterForm = () => (
    <>
      <Form.Group className="mb-3" controlId="formBasicNombre">
        <Form.Label>Nombre</Form.Label>
        <Form.Control
          type="text"
          placeholder="Ingrese nombre"
          name="nombre"
          value={formState.nombre}
          onChange={handleInputChange}
        />
      </Form.Group>

      {/* Add additional form groups for apellido, idOrRtn, correo, contrasena, and confirmarContrasena */}
      {/* ... */}
    </>
  );

  return (
    <>
      <button onClick={handleShow} className="icon-button">
        <img src={userIcon} alt="User" className="icon" />
        <p>{isLoginSelected ? 'Iniciar Sesión' : 'Registrarse'}</p>
      </button>

      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>
            {isLoginSelected ? 'Iniciar Sesión' : 'Registrarse'}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            {isLoginSelected ? renderLoginForm() : renderRegisterForm()}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Cerrar
          </Button>
          <Button variant="primary" onClick={handleClose}>
            {isLoginSelected ? 'Iniciar Sesión' : 'Registrarse'}
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default Login;
