          {/* Nombre de la empresa */}
          <span style={brandNameStyle}>Asian Imports</span>
        </div>
        <ul style={menuStyle}>
          <li style={menuItemStyle}>INICIO</li>
          <li style={menuItemStyle}>INFORMACION</li>
          <li style={menuItemStyle}>COMPRAR</li>
        </ul>
        <div>
          {/* Placeholder para los √≠conos de usuario y carrito */}
          <span style={menuItemStyle}>üë§</span> {/* Placeholder para el √≠cono de usuario */}
          <span style={menuItemStyle}>üõí</span> {/* Placeholder para el √≠cono de carrito */}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
