import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
  };

  const lineStyle = {
    height: '2px',
    width: '100px', // Ancho del icono de hamburguesa
    backgroundColor: '#F38413',
    position: 'absolute',
    bottom: 0,
    cursor: 'pointer',
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'block' : 'none') : 'flex',
    flexDirection: 'row',
    margin: 0,
    padding: 0,
    position: 'absolute',
    top: isSmallScreen ? '100%' : 0,
    left: 0,
    backgroundColor: 'white',
    width: '100%',
  };

  const menuItemStyle = {
    margin: '0 10px',
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  return (
    <nav style={navbarStyle}>
      <div onClick={toggleMenu} style={lineStyle}>
        ☰
      </div>
      <ul style={menuStyle}>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Inicio</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Información General</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Registrarse</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Contáctenos</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
