import React, { useState, useEffect } from 'react';
import './Navbar.css'; // Asegúrate de que la ruta al archivo CSS sea correcta

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);
  const [hoveredMenuItem, setHoveredMenuItem] = useState(null);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const topBarStyle = {
    backgroundColor: '#FFA500', // Naranja como en la imagen
    height: '20px',
    width: '100%',
  };

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: '#FFA500', // Naranja como en la imagen
    padding: '10px 20px', // Ajusta el padding según sea necesario
    position: 'relative',
    color: '#003366', // Azul oscuro para el texto
  };

  const lineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#004c8c', // Azul oscuro para la línea
    position: 'absolute',
    bottom: 0,
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'block' : 'none') : 'flex',
    flexDirection: isSmallScreen ? 'column' : 'row',
    margin: 0,
    padding: 0,
    position: 'absolute',
    top: isSmallScreen ? '100%' : '30px', // Ajusta la posición vertical del menú
    left: isSmallScreen ? 0 : '40px',
    backgroundColor: '#FFA500', // Naranja como en la imagen
    width: '100%',
  };

  const menuItemStyle = {
    margin: '0 10px',
    textDecoration: hoveredMenuItem === 'Contáctenos' && !isSmallScreen ? 'underline' : 'none',
    transition: 'text-decoration 0.3s',
    color: '#003366', // Azul oscuro para el texto
    cursor: 'pointer', // Agrega cursor pointer a los elementos del menú
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#003366', // Azul oscuro para los enlaces
  };

  const hamburgerStyle = {
    cursor: 'pointer',
    fontSize: '30px',
    color: '#F38413', // Ajusta el color según sea necesario
    marginRight: '1px',
  };

  return (
    <div>
      <div style={topBarStyle}></div>
      <nav style={navbarStyle}>
        <div style={lineStyle}></div>
        <div onClick={toggleMenu} style={hamburgerStyle}>☰</div>
        <ul style={menuStyle}>
          {/* Los íconos se pueden añadir aquí más tarde, con comentarios para indicar su posición */}
          {/* Asegúrate de dejar suficiente espacio y ajustar el margen/padding según sea necesario */}
          <li
            style={menuItemStyle}
            onMouseEnter={() => setHoveredMenuItem('Inicio')}
            onMouseLeave={() => setHoveredMenuItem(null)}
          >
            <a href="#" style={linkStyle}>Inicio</a>
          </li>
          {/* Repite los elementos <li> para otros enlaces del menú, ajustando el estado hoveredMenuItem según sea necesario */}
        </ul>
        {/* Espacio para íconos - comentado donde irían */}
        {/* <IconoDeUsuario /> */}
        {/* <IconoDeCarrito /> */}
        {/* <IconoDeBusqueda /> */}
      </nav>
    </div>
  );
};

export default Navbar;
