import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  // Estilo del contenedor del navbar
  const navbarContainerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#FFA500', // Naranja como en la imagen
    padding: '10px 20px',
  };

  // Estilo del contenedor del logo y el nombre de la empresa
  const logoAndBrandContainerStyle = {
    display: 'flex',
    alignItems: 'center',
    cursor: 'pointer', // Si el logo es clickeable
  };

  // Estilo para el nombre de la empresa
  const brandNameStyle = {
    marginLeft: '10px',
    fontFamily: "'Gravitas One', cursive", // Fuente 'Gravitas One'
    fontSize: '24px', // Tamaño de la fuente, ajusta según necesites
  };

  // Estilo para las opciones del menú
  const menuStyle = {
    listStyle: 'none',
    display: 'flex',
    margin: 0,
    padding: 0,
  };

  // Estilo para cada opción del menú
  const menuItemStyle = {
    padding: '0 20px',
    color: '#003366',
    cursor: 'pointer',
    fontFamily: "'Montserrat', sans-serif", // Fuente Montserrat
    fontSize: '16px', // Tamaño de la fuente, ajusta según necesites
  };

  return (
    <div style={navbarContainerStyle}>
      <div style={logoAndBrandContainerStyle}>
        {/* Logo de la empresa */}
        <img src={require('../assets/LogoPng.png')} alt="Logo" style={{ height: '40px' }} /> {/* Ajusta el tamaño según sea necesario */}
        {/* Nombre de la empresa */}
        <span style={brandNameStyle}>Asian Imports</span>
      </div>
      <ul style={menuStyle}>
        {/* Opciones del menú */}
        <li style={menuItemStyle}>INICIO</li>
        <li style={menuItemStyle}>INFORMACION</li>
        <li style={menuItemStyle}>COMPRAR</li>
      </ul>
      <div>
        {/* Placeholder para los íconos de usuario y carrito */}
        <span style={menuItemStyle}>👤</span> {/* Placeholder para el ícono de usuario */}
        <span style={menuItemStyle}>🛒</span> {/* Placeholder para el ícono de carrito */}
      </div>
    </div>
  );
};

export default Navbar;
