import React from 'react';

// Estilos CSS
const footerStyles = {
    backgroundColor: '#333',
    color: '#fdb254',
    padding: '20px',
    position: 'absolute',
    bottom: 0,
    width: '100%',
  };

const socialMediaStyles = {
  display: 'flex',
  alignItems: 'center',
  marginBottom: '10px',
};

const socialMediaLabelStyles = {
  marginRight: '10px',
};

const socialMediaIconStyles = {
  width: '30px', // Ajusta el tamaño de acuerdo a tus iconos
  margin: '0 10px',
};

const footerLinksStyles = {
  display: 'flex',
  flexWrap: 'wrap',
  justifyContent: 'center',
};

const footerLinkStyles = {
  margin: '5px',
  textDecoration: 'none',
  color: '#fff',
};

const copyrightStyles = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
};

const logoStyles = {
  width: '100px',
  marginBottom: '10px',
};

const footerComponentStyles = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
};

const Footer = () => {
  return (
    <footer style={footerStyles}>
      <div style={footerComponentStyles}>
        <div style={socialMediaStyles}>
          <span style={socialMediaLabelStyles}>Redes Sociales:</span>
          <img src="src/assets/facebook.png" alt="Facebook" style={socialMediaIconStyles} />
          <img src="src/assets/instagram.png" alt="Instagram" style={socialMediaIconStyles} />
          {/* Agrega más imágenes PNG para otras redes sociales si es necesario */}
        </div>
        <div style={footerLinksStyles}>
          <a href="#" style={footerLinkStyles}>Nuestra Historia</a>
          <a href="#" style={footerLinkStyles}>Acerca de Nosotros</a>
          <a href="#" style={footerLinkStyles}>Tiendas</a>
          <a href="#" style={footerLinkStyles}>Servicio al Cliente</a>
          <a href="#" style={footerLinkStyles}>Quejas y Reclamos</a>
          <a href="#" style={footerLinkStyles}>Política de Privacidad</a>
          <a href="#" style={footerLinkStyles}>Métodos de Pago</a>
        </div>
        <div style={copyrightStyles}>
          <img src="src/assets/LogoPng.png" alt="Logo de la empresa" style={logoStyles} />
          <p>&copy; {new Date().getFullYear()} Tu Empresa. Todos los derechos reservados.</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
