import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleResize = () => {
    setIsSmallScreen(window.innerWidth < 768);
  };

  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const menuIconStyle = {
    cursor: 'pointer',
    position: 'relative',
    width: '24px',
    height: '24px',
  };

  const menuIconLineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    transition: '0.3s',
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'block' : 'none') : 'flex',
    flexDirection: isSmallScreen ? 'column' : 'row',
    alignItems: 'center',
    margin: 0,
    padding: 0,
  };

  const menuItemStyle = {
    marginLeft: '10px', // Espacio horizontal entre elementos
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  const dropdownStyle = {
    backgroundColor: 'white',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
    padding: '10px',
    position: 'absolute',
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <button
        className="navbar-toggler"
        type="button"
        data-bs-toggle="collapse"
        data-bs-target="#navbarNav"
        onClick={toggleMenu}
      >
        <div style={menuIconStyle}>
          <div style={{ ...menuIconLineStyle, top: '0' }}></div>
          <div style={menuIconLineStyle}></div>
          <div style={{ ...menuIconLineStyle, top: '11px' }}></div>
        </div>
      </button>
      <div className={`collapse navbar-collapse ${isSmallScreen ? 'show' : ''}`} id="navbarNav">
        <ul className="navbar-nav" style={menuStyle}>
          <li className="nav-item">
            <a className="nav-link" href="#" style={linkStyle}>
              Inicio
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#" style={linkStyle}>
              Información General
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#" style={linkStyle}>
              Registrarse
            </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#" style={linkStyle}>
              Contáctenos
            </a>
          </li>
        </ul>
      </div>
      {isSmallScreen && menuOpen && (
        <div style={dropdownStyle}>
          <a href="#" style={linkStyle}>
            Inicio
          </a>
          <a href="#" style={linkStyle}>
            Información General
          </a>
          <a href="#" style={linkStyle}>
            Registrarse
          </a>
          <a href="#" style={linkStyle}>
            Contáctenos
          </a>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
