import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);
  const [selectedItem, setSelectedItem] = useState(null);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleResize = () => {
    setIsSmallScreen(window.innerWidth < 768);
  };

  const handleMenuItemHover = (item) => {
    if (isSmallScreen && menuOpen) {
      setSelectedItem(item);
    }
  };

  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'black', // Cambiar el color de fondo
    padding: '10px',
    position: 'fixed', // Añadir posición fija
    width: '100%', // Ocupar todo el ancho
    zIndex: 1000, // Añadir una z-index para que esté por encima del contenido
  };

  const menuIconStyle = {
    cursor: 'pointer',
    position: 'relative',
    width: '24px',
    height: '24px',
    display: isSmallScreen ? 'block' : 'none',
  };

  const menuIconLineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: 'white', // Cambiar el color de las líneas
    position: 'absolute',
    transition: '0.3s',
  };

  const menuItemStyle = {
    margin: '0 10px',
    cursor: 'pointer',
    transition: '0.3s',
    backgroundColor: selectedItem === menuItemStyle ? '#6b0606' : 'transparent', // Cambiar el color de fondo
    color: selectedItem === menuItemStyle ? 'white' : 'white', // Cambiar el color de texto
  };

  const linkStyle = {
    textDecoration: 'none',
    color: 'white',
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'flex' : 'none') : 'flex',
    flexDirection: isSmallScreen ? 'column' : 'row',
    alignItems: isSmallScreen ? 'center' : 'flex-end',
    margin: 0,
    padding: 0,
  };

  return (
    <nav style={navbarStyle}>
      <div style={menuIconStyle} onClick={toggleMenu}>
        <div style={{ ...menuIconLineStyle, top: '0', display: menuOpen ? 'none' : 'block' }}></div>
        <div style={{ ...menuIconLineStyle, top: '11px' }}></div>
        <div style={{ ...menuIconLineStyle, top: '22px', display: menuOpen ? 'none' : 'block' }}></div>
      </div>
      <ul style={menuStyle}>
        <li
          style={menuItemStyle}
          onMouseEnter={() => handleMenuItemHover(menuItemStyle)}
          onMouseLeave={() => handleMenuItemHover(null)}
        >
          <a href="#inicio" style={linkStyle}>Inicio</a>
        </li>
        <li
          style={menuItemStyle}
          onMouseEnter={() => handleMenuItemHover(menuItemStyle)}
          onMouseLeave={() => handleMenuItemHover(null)}
        >
          <a href="#sobre" style={linkStyle}>Capacitaciones</a>
        </li>
        <li
          style={menuItemStyle}
          onMouseEnter={() => handleMenuItemHover(menuItemStyle)}
          onMouseLeave={() => handleMenuItemHover(null)}
        >
          <a href="#contacto" style={linkStyle}>Sugerencias</a>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
