import React, { useState, useEffect } from 'react';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [isSmallScreen, setIsSmallScreen] = useState(window.innerWidth < 768);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleResize = () => {
    setIsSmallScreen(window.innerWidth < 768);
  };

  useEffect(() => {
    window.addEventListener('resize', handleResize);
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  const navbarStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'white',
    padding: '10px',
    position: 'relative',
    color: '#4179cc',
  };

  const menuIconStyle = {
    cursor: 'pointer',
    position: 'relative',
    width: '24px',
    height: '24px',
    display: isSmallScreen ? 'block' : 'none', // Mostrar solo en pantallas peque침as
  };

  const menuIconLineStyle = {
    height: '2px',
    width: '100%',
    backgroundColor: '#F38413',
    position: 'absolute',
    transition: '0.3s',
  };

  const menuItemStyle = {
    margin: '0 10px', // Espacio horizontal entre elementos
  };

  const linkStyle = {
    textDecoration: 'none',
    color: '#4179cc',
  };

  const menuStyle = {
    listStyle: 'none',
    display: isSmallScreen ? (menuOpen ? 'flex' : 'none') : 'flex',
    flexDirection: isSmallScreen ? 'column' : 'row',
    alignItems: isSmallScreen ? 'center' : 'flex-end',
    margin: 0,
    padding: 0,
  };

  return (
    <nav style={navbarStyle}>
      <div style={menuIconStyle} onClick={toggleMenu}>
        <div style={{ ...menuIconLineStyle, top: '0' }}></div>
        <div style={menuIconLineStyle}></div>
        <div style={{ ...menuIconLineStyle, top: '11px' }}></div>
      </div>
      <ul style={menuStyle}>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Inicio</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Informaci칩n General</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Registrarse</a></li>
        <li style={menuItemStyle}><a href="#" style={linkStyle}>Cont치ctenos</a></li>
      </ul>
    </nav>
  );
};

export default Navbar;
